import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import About from "./components/About";
import Alert from "./components/Alert";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css"; // Import your CSS file

function App() {
  const [mode, setMode] = useState("light");
  const [alert, setAlert] = useState(null);

  useEffect(() => {
    const root = document.documentElement;
    if (mode === "dark") {
      root.classList.add("bg-dark", "text-white");
      root.classList.remove("bg-light", "text-dark");
    } else {
      root.classList.add("bg-light", "text-dark");
      root.classList.remove("bg-dark", "text-white");
    }
  }, [mode]);

  const showAlert = (message) => {
    setAlert({ msg: message });
    setTimeout(() => {
      setAlert(null);
    }, 1000);
  };

  const changeBackground = (cls) => {
    document.body.classList.remove("bg-light", "bg-dark", "bg-danger", "bg-primary");
    document.body.classList.add("bg-" + cls);
    showAlert(`${cls.charAt(0).toUpperCase() + cls.slice(1)} mode activated`);
  };

  return (
    <>
      <Router>
        <Navbar
          title="TextUtils"
          about="About us"
          mode={mode}
          toggleMode={() => setMode(prevMode => (prevMode === "light" ? "dark" : "light"))}
          changeBackground={changeBackground}
        />
        <Alert alert={alert} />
        <div className="container my-5">
          <Routes>
            <Route path="/about" element={<About mode={mode} />} />
            <Route
              path="/"
              element={
                <TextForm
                  showAlert={showAlert}
                  heading="Enter the text to analyze"
                  mode={mode}
                />
              }
            />
          </Routes>
        </div>
      </Router>
    </>
  );
}

export default App;
