        about="About us"
        mode={mode}
        toggleMode={toggleMode} // Ensure this prop name matches the function name
      />
      <Alert alert={alert} />
      <div className="container my-5">


          <TextForm showAlert = {showAlert} heading="Enter the text to analyze" mode={mode} />


      </div>

    </>
  );
}

export default App;
