import React, { useState } from "react";
export default function TextForm(props) {
  const [text, setText] = useState("");
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLowClick = () => {
    let low = text.toLocaleLowerCase();
    setText(low);
  };

  const handleOnChange = (event) => {
    setText(event.target.value);
  };

  const speak = () => {
    let message = new SpeechSynthesisUtterance();
    message.text = text;
    window.speechSynthesis.speak(message);
  };
  const clear = ()=>{
   setText(" ")

}
const copy = ()=>{
let text = document.getElementById('myBox');
text.select();
navigator.clipc


}
  return (
    <>
      <div className="container">
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <label htmlFor="myBox" className="form-label">
            Example textarea
          </label>
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
          ></textarea>
        </div>
        <button className="btn btn-primary" onClick={handleUpClick}>
          Convert to Uppercase
        </button>
        <button className="btn btn-primary mx-2" onClick={handleLowClick}>
          Convert to lower case
        </button>
        <button type="submit" onClick={speak} className="btn btn-primary  my-2">
          Speak
        </button>
        <button className="btn btn-primary mx-2  my-2" onClick={clear}>Clear all</button>
        <button className="btn btn-primary" on onClick={copy}>Copy</button>
      </div>
      <div className="container my-3">
        <h2>your text summary</h2>
        <p>
          {text.split(" ").length} words and {text.length}characteristics
        </p>
        <p>{0.008 * text.split(" ").length}minutes to read</p>
        <h3>Preview</h3>
        <p>{text}</p>
      </div>
    </>
  );
}
