import React from "react";
import PropTypes from "prop-types";
import {Link} from 'react-router-dom'

export default function Navbar(props) {
  const textColor = props.mode === "light" ? "text-dark" : "text-white";
      const red = ()=>  {
if (mode == "light") {
  setMode('dark'),
  document.body.backgroundColor ="red"
  
} else {
  
setMode('light'),
document.body.backgroundColor

}



      }
  return (
    <nav
      className={`navbar navbar-expand-lg bg-${props.mode} navbar-dark`}
    >
      <div className="container-fluid">
        <Link className={`navbar-brand ${textColor}`} to="/">
          {props.title}
          </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <Link
                className={`nav-link active ${textColor}`}
                aria-current="page"
                to="/"
              >
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link className={`nav-link ${textColor}`} to="/about">
                {props.about}
              </Link>
            </li>
          </ul>
            <div>
              <button className="bg-danger rounded" mx-3 style = {{height :"25px", width :"25px" }} onClick={red}></button>
            </div>

          <div
            className={`form-check form-switch`}
          >
            <input
              className="form-check-input"
              type="checkbox"
              role="switch"
              id="flexSwitchCheckDefault"
              onClick={props.toggleMode}
            />
            <label
              className={`form-check-label ${textColor}`}
              htmlFor="flexSwitchCheckDefault"
            >
              Enable dark mode
            </label>
          </div>
        </div>
      </div>
    </nav>
  );
}

Navbar.propTypes = {
  title: PropTypes.string,
  about: PropTypes.string,
  mode: PropTypes.string.isRequired,
  toggleMode: PropTypes.func.isRequired,
};

Navbar.defaultProps = {
  title: "TextUtils",
  about: "About us",
};
