import React from "react";
import PropTypes from "prop-types";
import { Link } from 'react-router-dom';

export default function Navbar(props) {
  const textColor = props.mode === "light" ? "text-dark" : "text-white";

  return (
    <nav className={`navbar navbar-expand-lg bg-${props.mode} navbar-dark`}>
      <div className="container-fluid">
        <Link className={`navbar-brand ${textColor}`} to="/">
          {props.title}
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <Link className={`nav-link active ${textColor}`} aria-current="page" to="/">
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link className={`nav-link ${textColor}`} to="/about">
                {props.about}
              </Link>
            </li>
          </ul>
          <div>
            <button className="bg-primary rounded mx-1" style={{ height: "25px", width: "25px" }} onClick={() => props.toggleMode('primary')}></button>
            <button className="bg-danger rounded mx-1" style={{ height: "25px", width: "25px" }} onClick={() => props.toggleMode('danger')}></button>
            <button className="bg-dark rounded mx-1" style={{ height: "25px", width: "25px" }} onClick={() => props.toggleMode('dark')}></button>
            <button className="bg-light rounded mx-1" style={{ height: "25px", width: "25px" }} onClick={() => props.toggleMode('light')}></button>
          </div>
        </div>
      </div>
    </nav>
  );
}

Navbar.propTypes = {
  title: PropTypes.string,
  about: PropTypes.string,
  mode: PropTypes.string.isRequired,
  toggleMode: PropTypes.func.isRequired,
};

Navbar.defaultProps = {
  title: "TextUtils",
  about: "About us",
};
