import React, { useState } from "react";
import Navbar from "./components/Navbar";
import TextForm from "./components/textForm";
import About from "./components/About";
// import Alert from "./components/Alert"; // Ensure Alert component is correctly implemented and imported

function App() {
  const [mode, setMode] = useState("light");
  const [alert, setAlert] = useState(null);

const showAlert = (message)=>{
setAlert({
  msg: message
})
}

  const toggleMode = () => {
    if (mode === "light") {
      setMode("dark");
      document.body.style.backgroundColor = "#212529";
      showAlert('Dark mode activated')
    } else {
      setMode("light");
      document.body.style.backgroundColor = "white";
      showAlert('Light mode activated')

    }
  };

  return (
    <>
      <Navbar
        title="TextUtils"
        about="About us"
        mode={mode}
        toggleMode={toggleMode} // Ensure this prop name matches the function name
      />
      <Alert alert="Welcome to the alert" />
      <div className="container my-5">
        <TextForm heading="Enter the text to analyze" mode={mode} />
      </div>
    </>
  );
}

export default App;
